import json
import base64

# Parse the JSON content
content = r'{"requestContext": {"routeKey": "$default", "messageId": "IzEFvcTxDoECE3w=", "eventType": "MESSAGE", "extendedRequestId": "IzEFvG6gDoEERPQ=", "requestTime": "10/Apr/2025:08:25:46 +0000", "messageDirection": "IN", "stage": "dev", "connectedAt": 1744273285165, "requestTimeEpoch": 1744273546746, "identity": {"userAgent": "Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/135.0.0.0 Safari/537.36", "sourceIp": "80.111.230.103"}, "requestId": "IzEFvG6gDoEERPQ=", "domainName": "mqmdt7xebl.execute-api.eu-west-1.amazonaws.com", "connectionId": "IzDc3ebAjoECE3w=", "apiId": "mqmdt7xebl"}, "body": "{\\"action\\":\\"sample1\\",\\"cnt\\":4936,\\"recordingStartTime\\":\\"2025-04-10T08:21:25.770Z\\",\\"data\\":\\"\"}", "isBase64Encoded": false}'

parsed_json = json.loads(content)
body = json.loads(parsed_json["body"])

# Extract and decode the audio data
audio_data_base64 = body["data"]
audio_data = base64.b64decode(audio_data_base64)

# Save the audio data to a file
output_file = "c:\\workspace\\thedigitalmindhub\\output_audio.raw"
with open(output_file, "wb") as f:
    f.write(audio_data)

print(f"Audio data saved to {output_file}")